import { useState } from 'react';
import StoreContext from './storeContext';

function GlobalState(props) {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState({});

  return (
    <StoreContext.Provider
      value={{
        cart: cart,
      }}
    >
      {props.children}
    </StoreContext.Provider>
  );
}

export default GlobalState;
